import { __decorate } from "tslib";
import __NG_CLI_RESOURCE__0 from "./search-movie.component.html?ngResource";
import __NG_CLI_RESOURCE__1 from "./search-movie.component.css?ngResource";
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { MoviesService } from '../movies.service';
import { BackendService } from '../backend.service';
let SearchMovieComponent = class SearchMovieComponent {
    constructor(service, route, aservice) {
        this.service = service;
        this.route = route;
        this.aservice = aservice;
        this.page = 1;
        this.tableSize = 10;
        this.movies = [];
        this.status = this.aservice.adminStatus;
    }
    bookTicket(movie) {
        this.service.movie = movie;
        this.route.navigate(['bookticket']);
    }
    ngOnInit() {
        this.getMovies();
    }
    getMovies() {
        console.log(this.service.movieName);
        this.service.getMovies(this.service.movieName).subscribe(data => {
            this.movies = data;
            console.log(this.movies);
            if (this.movies.length === 0)
                this.size = 0;
        }, error => {
        });
    }
    deleteMovie(movieName, theatreName) {
        this.service.deleteMovie(movieName, theatreName).subscribe(data => {
            this.message = data;
            alert(data);
            this.aservice.adminStatus = true;
            this.getMovies();
        });
    }
    movieDetails(movie) {
        this.service.movie = movie;
        this.route.navigate(['moviedetails']);
    }
    onTableDataChange(event) {
        this.page = event;
        this.getMovies();
    }
};
SearchMovieComponent.ctorParameters = () => [
    { type: MoviesService },
    { type: Router },
    { type: BackendService }
];
