import { __decorate } from "tslib";
import __NG_CLI_RESOURCE__0 from "./movieslist.component.html?ngResource";
import __NG_CLI_RESOURCE__1 from "./movieslist.component.css?ngResource";
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BackendService } from '../backend.service';
import { MoviesService } from '../movies.service';
let MovieslistComponent = class MovieslistComponent {
    constructor(service, route, aservice) {
        this.service = service;
        this.route = route;
        this.aservice = aservice;
        this.name = '';
        this.page = 1;
        this.tableSize = 10;
        this.movies = [];
        this.status = this.aservice.adminStatus;
    }
    getMoviesList() {
        this.service.getAllMovies().subscribe(data => {
            this.movies = data;
            console.log(data);
        });
    }
    getMovie() {
        console.log(this.name);
        if (this.name == '') {
            alert("Please Enter movie name");
        }
        else {
            this.service.movieName = this.name;
            this.route.navigate(['search']);
        }
    }
    onTableDataChange(event) {
        this.page = event;
        this.getMoviesList();
    }
