import { __decorate } from "tslib";
import __NG_CLI_RESOURCE__0 from "./forgotpassword.component.html?ngResource";
import __NG_CLI_RESOURCE__1 from "./forgotpassword.component.css?ngResource";
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BackendService } from '../backend.service';
import { ForgotPassword } from '../ForgotPassword';
let ForgotpasswordComponent = class ForgotpasswordComponent {
    constructor(service, route) {
        this.service = service;
        this.route = route;
        this.errorMessage = 'Password is not matching.';
        this.errormsg = '';
        //updatePassword!:ForgotPassword
        this.form = {
            loginId: '',
            password: '',
            confirmPassword: ''
        };
    }
    ngOnInit() {
    }
    forgotPassword() {
        if (this.form.password != this.form.confirmPassword) {
            //error("password not matching")
            this.status = false;
            this.errorMessage = "Password is not matching";
        }
        else {
            this.status = true;
            console.log(this.form.password);
            //updatePassword :new ForgotPassword(this.form.password,this.form.confirmPassword);
            //console.log(updatePassword)
            this.service.changePassword(this.form.loginId, new ForgotPassword(this.form.password, this.form.confirmPassword)).subscribe(data => {
                this.message = data;
                console.log(data);
                if (this.message)
                    alert("Password changed successfully");
                this.route.navigate(['']);
            }, error => {
                this.errormsg = "Please Enter Correct Login ID";
            });
        }
    }
    cancel() {
        this.route.navigate(['']);
    }
};
ForgotpasswordComponent.ctorParameters = () => [
    { type: BackendService },
    { type: Router }
];
